mapper.select:
select ID, code, name, lastPrice,todayUpOrDown,time from industry_analy

	1=1
	and code=#{code}
	and name=#{name}
	and time=#{time}
	ORDER BY todayUpOrDown DESC
	LIMIT #{pageBeg},#{pageSize}

select ID, code, name, lastPrice,todayUpOrDown,time from industry_analy

	1=1
	and code=#{code}
	and time=#{time}

select ID, code, name, lastPrice,todayUpOrDown,time from industry_analy

	1=1
	and code=#{cac.code}
	and name=#{cac.name}

ORDER BY time DESC limit ${pageNo},${pageSize}

select count(1) from industry_analy

	1=1
	and code=#{cac.code}
	and name=#{cac.name}
